import React from 'react'

function MainForm(props) {


    return (
        <div>
            <div>
                <select onChange={props.makeSelection} name="base" value={props.base}>
                    {
                        props.state.currencies ? props.state.currencies.map(currency => <option key={currency} value={currency}>
                            {currency}
                        </option>)
                            : null
                    }
                </select>
                <input onChange={props.changeValue} value={props.state.value} />
            </div>
            <div>
                <select onChange={props.makeSelection} name="target" value={props.state.target}>
                    {
                        props.state.currencies ? props.state.currencies.map(currency => <option key={currency} value={currency}>
                            {currency}
                        </option>)
                            : null
                    }
                </select>
                <input disabled={true} value={props.state.converted} />
            </div>
        </div>
    )

}

export default MainForm
