import React from 'react'
import {Link} from 'react-router-dom'
import('../App.css')




class Currencies extends React.Component {

    constructor(props){
        super(props)

        this.state = {
            base: "IDR",
            target:"USD",
            value: 0,
            currencies : [],
            converted: 0
        }
    }
    componentDidMount(){
        fetch(`https://api.exchangeratesapi.io/latest`)
        .then(response => response.json())
        .catch(error => console.error('Error:', error))
        .then(data=> {
           this.setState({
               currencies:[...Object.keys(data.rates), "EUR" ]
           })
        })
    }
    render(){
        return (
            <div className='App'>
                        <h1>Conversion Site Landing Page</h1>

                <div>
                    <select onChange={this.makeSelection} name="base" value={this.state.base}>
                         {this.state.currencies.map(currency => <option key={currency} value={currency}>
                             {currency} </option>)} </select>
                    <input onChange={this.changeValue} value={this.state.value}/>
                </div>
                <div>
                    <select onChange={this.makeSelection} name="target" value={this.state.target}>
                        {this.state.currencies.map(currency => <option key={currency} value={currency}>
                            {currency} </option>)} </select>
                    <input disabled={true} value={this.state.converted}/>
                </div>
                <Link to='/'>Back To Home</Link>

            </div>
        )
    }

    changeValue = (event) => {
        this.setState({
            value: event.target.value
        }, this.recalculate)
    }

    makeSelection = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        }, this.recalculate)
    }

    recalculate = () => {
        const value = parseFloat(this.state.value);

        if (isNaN(value)) {
            return;
        }

        fetch(`https://api.exchangeratesapi.io/latest?base=${this.state.base}`)
        .then(res => res.json())
        .then(data => {
            this.setState({
                converted: data.rates[this.state.target] * value
            })
        })
    }

